class ClickableTooltipWidgetState extends State<ClickableTooltipWidget> {
  bool nest = false;
  final Key key = GlobalKey();

  @override
  Widget build(BuildContext context) {
    final Widget button2 = TooltipButton(key: key, index: 2);
    return Scaffold(
