}

class TooltipButton extends StatefulWidget {
  const TooltipButton({required this.index, Key? key}) : super(key: key);

  final int index;

  @override
  State<TooltipButton> createState() => _TooltipButtonState();
}

class _TooltipButtonState extends State<TooltipButton> {
  bool show = false;

  static final List<Color> _colors = [Colors.green, Colors.blue, Colors.yellow];

  @override
  Widget build(BuildContext context) {
    return OverlayPortal(
