class _CustomRenderWrap extends RenderWrap {
  bool _superDoingLayout = false;

  // Get from children
  _RenderWithOverride get overrideableRenderChild => ... 

  @override
  double computeMinIntrinsicWidth(double height) {
    if (_superDoingLayout) return super.computeMinIntrinsicWidth();

    overrideableRenderChild.sizeOverride = Size.zero;

    // This will call `overrideableRenderChild.computeMinIntrinsicWidth` down the line.
    // Now the cached computation on `overrideableRenderChild` is polluted with the size override
    // for subsequent calls.
    final computed = super.computeMinIntrinsicWidth(height);

    overrideableRenderChild.sizeOverride = null;

    return computed;
  }


  @override
